DELIMITER $$
CREATE TRIGGER `buildings_delete_after` AFTER DELETE ON `buildings` FOR EACH ROW BEGIN
UPDATE locations SET building_count=building_count-1 WHERE id=OLD.building_location_id;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `buildings_insert_after` AFTER INSERT ON `buildings` FOR EACH ROW BEGIN
UPDATE locations SET building_count=building_count+1 WHERE id=NEW.building_location_id;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `buildings_update_after` AFTER UPDATE ON `buildings` FOR EACH ROW BEGIN
UPDATE locations SET building_count=building_count+1 WHERE id=NEW.building_location_id;
UPDATE locations SET building_count=building_count-1 WHERE id=OLD.building_location_id;
END
$$
DELIMITER ;

