DELIMITER $$
CREATE TRIGGER `projects_delete_after` AFTER DELETE ON `projects` FOR EACH ROW BEGIN
UPDATE locations SET project_count=project_count-1 WHERE id=OLD.project_location_id;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `projects_insert_after` AFTER INSERT ON `projects` FOR EACH ROW BEGIN
UPDATE locations SET project_count=project_count+1 WHERE id=NEW.project_location_id;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `projects_update_after` AFTER UPDATE ON `projects` FOR EACH ROW BEGIN
DECLARE rowcount INT;
set rowcount =(SELECT count('x') FROM projects WHERE project_location_id=NEW.project_location_id);
UPDATE locations SET project_count=rowcount  WHERE id=NEW.project_location_id;
END
$$
DELIMITER ;

